int CMainFrame::OnCreate(LPCREATESTRUCT lpCreateStruct)
{
	if (CFrameWnd::OnCreate(lpCreateStruct) == -1)
		return -1;
	
	if (!m_wndToolBar.CreateEx(this, TBSTYLE_FLAT, WS_CHILD | WS_VISIBLE | CBRS_TOP
		| CBRS_GRIPPER | CBRS_TOOLTIPS | CBRS_FLYBY | CBRS_SIZE_DYNAMIC) ||
		!m_wndToolBar.LoadToolBar(IDR_MAINFRAME))
	{
		TRACE0("Failed to create toolbar\n");
		return -1;      // fail to create
	}

	if (!m_wndStatusBar.Create(this) ||
		!m_wndStatusBar.SetIndicators(indicators,
		  sizeof(indicators)/sizeof(UINT)))
	{
		TRACE0("Failed to create status bar\n");
		return -1;      // fail to create
	}

	// TODO: Delete these three lines if you don't want the toolbar to
	//  be dockable
	m_wndToolBar.EnableDocking(CBRS_ALIGN_ANY);
	EnableDocking(CBRS_ALIGN_ANY);
	DockControlBar(&m_wndToolBar);

	return 0;
}

BOOL CMainFrame::OnCreateClient( LPCREATESTRUCT /*lpcs*/,
	CCreateContext* pContext)
{
	/*
	return m_wndSplitter.Create( this,
		2, 2,                 // TODO: adjust the number of rows, columns
		CSize( 10, 10 ),      // TODO: adjust the minimum pane size
		pContext );
	*/

	CRect rect;
	GetClientRect(rect);
	// 좌우 2개의 분할 윈도우(split window)를 생성한다.
	if(!m_wndSplitter.CreateStatic(this, 1, 2))
		return FALSE;
	// 왼쪽 분할 윈도우를 CHelloView와 연결한다.
	// 크기는 클라이언트 영역의 반으로 한다.
	if(!m_wndSplitter.CreateView(0, 0, RUNTIME_CLASS(CHelloView),
		CSize(rect.right/2, rect.bottom/2), pContext))
		return FALSE;
	// 오른쪽 분할 윈도우를 CTextFormView와 연결한다.
	// 크기는 클라이언트 영역의 반으로 한다.
	if(!m_wndSplitter.CreateView(0, 1, RUNTIME_CLASS(CTextFormView),
		CSize(rect.right/2, rect.bottom/2), pContext))
		return FALSE;
	// 왼쪽 분할 윈도우가 포커스를 받게 한다.
	SetActiveView((CView*)m_wndSplitter.GetPane(0,0));

	return TRUE;
}

